(function ($) {
    $.fn.oneCarousel = function (c) {
        var f = this;
        var e = $.extend({ easeIn: "fadeInLeft", pause: "hover", interval: 5000 }, c);
        f.carousel({ pause: e.pause, interval: e.interval });
        var b;
        var d;
        f.find(".carousel-caption").children().each(function (g) {
            $(this).css("opacity", 0);
        });

        $(".carousel-caption", f.find(".active")).children().each(function (g) {
            if ($(this).data("animate")) { d = $(this).data("animate"); }
            else { d = e.easeIn; }
            if (false) {
                $(this).css("opacity", 0);
                $(this).show().animate({ opacity: 1 }, 200 + 200 * g);
            } else {
                $(this).removeClass(d).addClass("animate" + g + " " + d);

            }
            b = $(this);
        });
        f.on("slid.bs.carousel", function (g) {
            if (b) {
                b.find(".carousel-caption").children().each(function (h) {
                    if ($(this).data("animate")) {
                        d = $(this).data("animate");
                    } else {
                        d = e.easeIn;
                    }
                    $(this).removeClass(d).css("opacity", 0);
                });
            }
            $(".carousel-caption", f.find(".active")).children().each(function (h) {
                if ($(this).data("animate")) {
                    d = $(this).data("animate");
                } else {
                    d = e.easeIn;
                }
                if (false) {
                    $(this).css("opacity", 0);
                    $(this).show().animate({ opacity: 1 }, 200 + 200 * h);
                } else {
                    $(this).css("opacity", 0).removeClass(d).addClass("animate" + h + " " + d);
                }
            });
            b = $(this);
        });
        return this;
    };
})(jQuery);